import React, { useState } from "react";
import ProfileForm from "./ProfileForm";

function SchemeCard({ scheme }) {
  return (
    <div style={{ border: "1px solid #ccc", padding: "15px", marginBottom: "10px", borderRadius: "8px" }}>
      <h3>{scheme.name}</h3>
      <p>{scheme.description}</p>
      {scheme.benefit && <p><strong>Benefit:</strong> {scheme.benefit}</p>}
      {scheme.application_link && scheme.application_link !== "#" && (
        <a href={scheme.application_link} target="_blank" rel="noopener noreferrer">
          Apply Here
        </a>
      )}
    </div>
  );
}

function Dashboard() {
  const [schemes, setSchemes] = useState([]);
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (formData) => {
    setLoading(true);
    setSchemes([]);
    try {
      const res = await fetch("http://127.0.0.1:5000/ai_schemes", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData)
      });

      const data = await res.json();
      if (data.ai_suggestions) {
        setSchemes(data.ai_suggestions);
      }
    } catch (err) {
      console.error(err);
      setSchemes([{ name: "Error", description: "Failed to fetch AI suggestions.", benefit: "", application_link: "#" }]);
    }
    setLoading(false);
  };

  return (
    <div style={{ maxWidth: "600px", margin: "20px auto", fontFamily: "Arial, sans-serif" }}>
      <h1>Sahay Sarthi - Find Matching Government Schemes</h1>
      <ProfileForm onSubmit={handleSubmit} />
      {loading && <p>Loading AI suggestions...</p>}
      {schemes.length > 0 && (
        <div style={{ marginTop: "20px" }}>
          <h2>Matching Schemes:</h2>
          {schemes.map((scheme, index) => (
            <SchemeCard key={index} scheme={scheme} />
          ))}
        </div>
      )}
    </div>
  );
}

export default Dashboard;
