import React, { useState } from "react";
import ProfileForm from "./ProfileForm";
import SchemeCard from "./SchemeCard";

function Dashboard() {
  const [schemes, setSchemes] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const handleFormSubmit = async (formData) => {
    setLoading(true);
    setError("");
    setSchemes([]);

    try {
      const response = await fetch("http://127.0.0.1:5000/find_schemes", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });

      if (!response.ok) {
        throw new Error("Network response was not ok");
      }

      const data = await response.json();

      if (data.schemes && data.schemes.length > 0) {
        setSchemes(data.schemes);
      } else {
        setError("No matching schemes found.");
      }
    } catch (err) {
      setError("Failed to fetch schemes. Please try again.");
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ maxWidth: "600px", margin: "0 auto", padding: "20px" }}>
      <h1>Sahay Sarthi - Find Matching Government Schemes</h1>
      <ProfileForm onSubmit={handleFormSubmit} />
      {loading && <p>Loading schemes...</p>}
      {error && <p style={{ color: "red" }}>{error}</p>}
      {schemes.map((scheme) => (
        <SchemeCard key={scheme.id} scheme={scheme} />
      ))}
    </div>
  );
}

export default Dashboard;
