import React, { useState } from "react";
import ProfileForm from "./ProfileForm";
import SchemeCard from "./SchemeCard";

function Dashboard() {
  const [schemes, setSchemes] = useState([]);
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (formData) => {
    setLoading(true);
    setSchemes([]);

    try {
      const res = await fetch("http://localhost:5000/find_schemes", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });

      const data = await res.json();
      if (data.schemes) {
        setSchemes(data.schemes);
      }
    } catch (err) {
      console.error(err);
      setSchemes([{ name: "Error", description: "Failed to fetch schemes." }]);
    }

    setLoading(false);
  };

  return (
    <div style={{ maxWidth: "600px", margin: "20px auto", fontFamily: "Arial, sans-serif" }}>
      <h1>Sahay Sarthi - Find Matching Government Schemes</h1>
      <ProfileForm onSubmit={handleSubmit} />
      {loading && <p>Loading schemes...</p>}
      {schemes.length > 0 && (
        <div style={{ marginTop: "20px" }}>
          <h2>Matching Schemes:</h2>
          {schemes.map((scheme, index) => (
            <SchemeCard key={index} scheme={scheme} />
          ))}
        </div>
      )}
    </div>
  );
}

export default Dashboard;
