import React, { useState } from "react";
import ProfileForm from "./ProfileForm";
import SchemeCard from "./SchemeCard";

function Dashboard() {
  const [schemes, setSchemes] = useState([]);

  const handleFormSubmit = async (formData) => {
    try {
      const response = await fetch("http://127.0.0.1:5000/find_schemes", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });

      if (!response.ok) {
        throw new Error("Failed to fetch");
      }

      const data = await response.json();
      setSchemes(data.schemes || []);
    } catch (error) {
      console.error(error);
      alert("Error fetching schemes from backend");
    }
  };

  return (
    <div style={{ padding: "20px", fontFamily: "Arial" }}>
      <h1>Sahay Sarthi - Find Matching Government Schemes</h1>
      <ProfileForm onSubmit={handleFormSubmit} />
      <div style={{ marginTop: "20px" }}>
        {schemes.length === 0 ? (
          <p>No schemes found.</p>
        ) : (
          schemes.map((scheme) => <SchemeCard key={scheme.id} scheme={scheme} />)
        )}
      </div>
    </div>
  );
}

export default Dashboard;
