import React from "react";

function SchemeCard({ scheme }) {
  return (
    <div style={{
      border: "1px solid #ccc",
      borderRadius: "10px",
      padding: "15px",
      margin: "10px 0",
      boxShadow: "0 2px 5px rgba(0,0,0,0.1)",
      backgroundColor: "#f9f9f9"
    }}>
      <h3 style={{ margin: "0 0 10px 0" }}>{scheme.name}</h3>
      <p style={{ margin: "0 0 5px 0" }}>{scheme.description}</p>
      <p style={{ fontWeight: "bold", margin: "0 0 10px 0" }}>Benefit: {scheme.benefit}</p>
      <a href={scheme.application_link} target="_blank" rel="noopener noreferrer"
         style={{
           textDecoration: "none",
           color: "white",
           backgroundColor: "#007BFF",
           padding: "5px 10px",
           borderRadius: "5px"
         }}>
        Apply Here
      </a>
    </div>
  );
}

export default SchemeCard;
